$(document).ready(function(){


  new Hammer( $( ".fixSafariOverflow" )[ 0 ], {
        domEvents: true
      } );
      var current = 1;
      var pgs = $( ".home-page-slider" );
      $( ".fixSafariOverflow" ).on( "swipeleft", function( e ) {
       if ( pgs[ current + 1 ] ) {
         pgs.removeClass( "home" );
         pgs.addClass('hidden')
         pgs.eq( ++current ).addClass( "home" );
         pgs.eq(current).removeClass( "hidden" );
       }
      } );
      $( ".fixSafariOverflow" ).on( "swiperight", function( e ) {
       if ( pgs[ current - 1 ] ) {
         pgs.removeClass( "home" );
         pgs.addClass('hidden')
         pgs.eq( --current ).addClass( "home" );
         pgs.eq(current).removeClass( "hidden" );
       }
      } );


  var rightPageSlide = document.getElementById('rightPage');
  var slider = new Hammer(rightPageSlide)
  slider.on("panleft panright tap press", function(ev) {
     if(ev.type=="panleft"){
       // console.log("panleft")
     }else if(ev.type=="panright"){
       // console.log("panright")
       $('#rightPage').removeClass("rightPage", homeAfterRight());
     }
  });//End RightPage slider

  $("._scoreboard_challenges input.release-checkbox").on('click',function(){
    $(this).css({"background-color":"green","box-shadow": "0px 0px 0px black","border":"black 1px solid" });
  });
// "box-shadow": "0px 0px 2px black"
// "border":"black"

  addNoteToDatabase()
  addEngineeringEmpathyPoint();
  completeChallengeRelease();
});//END DOCUMENT READY


function homeAfterLeft() {
  setTimeout(function(){
    $('#home').removeClass("afterSwipeLeftHome");
  }, 75);
}


function homeAfterRight(){
    setTimeout(function(){
    $('#home').removeClass("afterSwipeRightHome");
  }, 75);
}

var addNoteToDatabase = function(){
  $('.note-form').on('submit', function(event){
    event.preventDefault();
    // debugger;
    var data = $(this).serialize()
    $(this).children().val('')
    var method = $(this).attr('method')
    var url = $(this).attr('action')
    $.ajax({
      method: method,
      url: url,
      data: data
    }).done(function(response){
      alert('Note successfully created')
    })
  })
}

var addEngineeringEmpathyPoint = function(){
  $('.ee-btns').on('click', function(event){
    event.preventDefault()
    var url = $(this).attr('action')
    $.ajax({
      method: 'patch',
      url: url
    }).done(function(response){
      $('#points-container').html(response);
    })
  })
}

var completeChallengeRelease = function(){
  $('.release-checkbox').on('click', function(event){
    var url = $(this).parent().attr('action')
    $.ajax({
      url: url,
      method: "PATCH"
    }).done(function(response){
      // $('#leftPage').html(response)
      $('#points-container').html(response);
    })
  })
}

